import React from 'react'
import '../css/Home.css'
import { Navbar, NavbarBrand, Nav, NavItem, NavLink, UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap'
import fire from '../../config/fire'
import classnames from 'classnames'

class Home extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            tab: 'welcome'
        }

        this.logout = this.logout.bind(this);
    }

    render() {
        return (
            <div>
                {this.getMenu()}
                {this.getBody()}
            </div>
        )
    }

    logout() {
        fire.auth().signOut();
        this.props.history.push("/");
    }

    getMenu() {
        return (
            <Navbar color="light" light expand="md">
                <div className = "container">

                <NavbarBrand href="/home">Flight School</NavbarBrand>

                <Nav className="ml-auto" navbar>
                    <NavItem>
                        <NavLink href="/guide" className="mr-5">Guide</NavLink>
                    </NavItem>

                    <NavItem>
                        <NavLink href="/about" className="mr-5">About</NavLink>
                    </NavItem>

                    <UncontrolledDropdown nav inNavbar>
                        <DropdownToggle nav caret>
                            Account
                        </DropdownToggle>

                        <DropdownMenu right>
                            <DropdownItem>
                            Account Details
                            </DropdownItem>

                            <DropdownItem divider />

                            <DropdownItem onClick={ this.logout }>
                            Logout
                            </DropdownItem>
                        </DropdownMenu>
                    </UncontrolledDropdown>
                </Nav>

                </div>
            </Navbar>
        )
    }

    getBody() {
        return (
            <div className="container mt-5">
                <Nav tabs>
                    <NavItem>
                        <NavLink className={classnames({ active: this.state.tab === 'welcome' })} onClick={() => { toggleTab("welcome") }}>
                        Welcome
                        </NavLink>
                    </NavItem>

                    <NavItem>
                        <NavLink className={classnames({ active: this.state.tab === 'blocks' })} onClick={() => { toggleTab("blocks") }}>
                        Blocks
                        </NavLink>
                    </NavItem>

                    <NavItem>
                        <NavLink className={classnames({ active: this.state.tab === 'team' })} onClick={() => { toggleTab("team") }}>
                        Team
                        </NavLink>
                    </NavItem>
                </Nav>
            </div>
        )
    }

    toggleTab(newTab) {
        this.setState({
            tab: newTab
        });
    }
}

export default Home
