import React, { Component } from 'react';
import { Link } from 'react-router-dom';

// css
import '../css/Navbar.css';

// Material UI
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import withStyles from '@material-ui/styles/withStyles';
import Typography from '@material-ui/core/Typography';
import AccountCircleIcon from '@material-ui/icons/AccountCircleIcon';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const styles = {
    // This group of buttons will be aligned to the right
    rightToolbar: {
      marginLeft: 'auto',
      marginRight: -12,
    },
    menuButton: {
      marginRight: 16,
      marginLeft: -12,
    },
};

class Navbar extends Component {

    constructor() {
        super();

        this.state = {
            anchorEl: null,
        }
    }

    render() {
        const { classes } = this.props;

        return (
            <AppBar>
                <Toolbar>
                    <Typography variant='h6' color="inherit">Flight School</Typography>

                    <section className={classes.rightToolbar}>
                        <Button color="inherit" component={Link} to="/team" >
                            Team
                        </Button>
                        <Button color="inherit" component={Link} to="/blocks" >
                            Training
                        </Button>
                        <Button color="inherit" onClick={this.handleClick}>
                            <AccountCircleIcon />
                        </Button>
                        <Menu
                            id='fade-menu'
                            anchorEl={this.state.anchorEl}
                            keepMounted
                            open={Boolean(this.state.anchorEl)}
                            onClose={this.handleClose}
                        >
                            <MenuItem onClick={this.handleClose}>My Profile</MenuItem>
                            <MenuItem onClick={this.handleClose}>Logout</MenuItem>
                        </Menu>
                    </section>
                </Toolbar>
            </AppBar>
        );
    }

    handleClick = event => {
        this.setState({
            anchorEl: event.currentTarget
        })
    };

    handleClose = () => {
        this.setState({
            anchorEl: null
        })
    };
}

export default withStyles(styles)(Navbar)
