import React from 'react'
import '../css/App.css'
import fire from '../../config/fire'
import { Button, Form, FormGroup, Label, Input, Alert, Jumbotron, Container } from 'reactstrap';

class SignIn extends React.Component {

    constructor(props) {
        super(props);

        this.login = this.login.bind(this);
        this.handleChange = this.handleChange.bind(this);

        this.state = {
            email: '',
            password: '',
            alert: '',
            isOpen: false
        }
    }

    login(e) {
        e.preventDefault();
        fire.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u)=>{ 
            this.props.history.push("/home")
        }).catch((error) => {
            this.setState({
                alert: "Invalid email or password. Please try again",
                isOpen: true
            });
            // console.log(error);
        });
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value });
    }

    render() {
        return (

            <div>
                <Jumbotron>
                <Container fluid>
                    <h1 className="display-3">Fluid jumbotron</h1>
                    <p className="lead">This is a modified jumbotron that occupies the entire horizontal space of its parent.</p>
                </Container>
                </Jumbotron>


            <Form className="login-form mt-3" onSubmit = {this.login}>
                <h1 className="text-center" >Flight School</h1>
                <h2 className="text-center" >Log In</h2>

                <div className="mt-4">
                <Alert color="danger" isOpen={this.state.isOpen}>
                    {this.state.alert}
                </Alert>
                </div>

                <FormGroup className="mt-4">
                    <Label>Email</Label>
                    <Input name="email" type="email" placeholder="Email" onChange={this.handleChange}/>
                </FormGroup>

                <FormGroup>
                    <Label>Password</Label>
                    <Input name="password" type="password" placeholder="password" onChange={this.handleChange}/>
                </FormGroup>


                <div className="mt-5">
                    <Button className="btn-lrg btn-dark btn-block mt-4" onClick = {this.login}>Log In</Button>

                    <div className="text-center mt-3">
                        <a href="/signup">Sign Up</a>
                    </div>
                </div>
            </Form>
            </div>
        )
    }
}

export default SignIn
