import React from 'react'
import { Table } from 'reactstrap'
import '../css/Home.css'

class RPE extends React.Component {

    constructor(props) {
        super(props);

        this.createChart = this.createChart.bind(this);
    }

    render() {
        return this.chart()
    }

    chart() {
        // create array of 9 for y axis
        let yAxis = new Array(9);

        // create array of 13 for each index of y axis
        for(let i = 0; i < yAxis.length; i++) {
            yAxis[i] = new Array(13);
        }

        // @ sign for [0][0]
        yAxis[0][0] = "@"

        // populate rpe
        let rpe = 10.0;
        for(let i = 1; i < yAxis.length; i++) {
            yAxis[i][0] = rpe;
            rpe = rpe -.5
        }

        // populate rep count
        let reps = 1;
        for(let i = 1; i < yAxis[0].length; i++) {
            yAxis[0][i] = reps;
            reps = reps + 1;
        }

        // populate first row
        yAxis[1][1] = 100;
        yAxis[1][2] = 95.5;
        yAxis[1][3] = 92.2;
        yAxis[1][4] = 89.2;
        yAxis[1][5] = 86.3;
        yAxis[1][6] = 83.7;
        yAxis[1][7] = 81.1;
        yAxis[1][8] = 78.6;
        yAxis[1][9] = 76.2;
        yAxis[1][10] = 73.9;
        yAxis[1][11] = 70.7;
        yAxis[1][12] = 68;

        // populate last column
        yAxis[2][12] = 66.7;
        yAxis[3][12] = 65.3;
        yAxis[4][12] = 64;
        yAxis[5][12] = 62.6;
        yAxis[6][12] = 61.3;
        yAxis[7][12] = 59.9;
        yAxis[8][12] = 58.6;

        // populate rest of 2D array
        for(let i = 2; i < yAxis.length; i++) {
            for(let j = 1; j < yAxis[i].length-1; j++) {
                yAxis[i][j] = ((yAxis[i-1][j] - yAxis[i-1][j+1]) / 2) + yAxis[i-1][j+1];
            }
        }

        return this.createChart(yAxis);
    }

    createChart(yAxis) {
        return <div className="container mt-4 text-color-black">
            <div className="text-center">
                <h1>RPE Chart</h1>
            </div>

            <Table>
                <thead>
                    <tr>
                        {this.returnHead(yAxis)}
                    </tr>
                </thead>

                <tbody>

                </tbody>
            </Table>
        </div>
    }

    returnHead(yAxis) {
        return yAxis[0].map(head => {
            return (
                <th>
                    {head}
                </th>
            )
         })
    }

    returnBody(yAxis) {

    }
}

export default RPE
